<script>
  import SVGComponent from "../components/SVGComponent.svelte";
  import ChangeEmailModal from "../components/ChangeEmailModal.svelte";
  import ChangePasswordModal from "../components/ChangePasswordModal.svelte";
  import EnableGoogleAuthenticatorModal from "../components/EnableGoogleAuthenticatorModal.svelte";
  import EnableTelegramModal from "../components/EnableTelegramModal.svelte";
    import EnablePhoneModal from "../components/EnablePhoneModal.svelte";


  let data = {};
  let showChangeEmailModal = false;
  let showChangePasswordModal = false;
  let showGoogleAuthModal = false;
  let showTelegramModal = false;
  let showPhoneModal = false;

  function onMount() {
    if (window.CEF) {
      window.CEF.on("C2W:Menu_F4_Settings_Secure_Secure:SetData", handleSetData);
    }
    if (window.CEF && window.CEF.call) {
      window.CEF.call("W2C:Menu_F4_Settings_Secure_Secure:Ready");
    }
  }

  function onUnmount() {
    if (window.CEF) {
      window.CEF.off("C2W:Menu_F4_Settings_Secure_Secure:SetData", handleSetData);
    }
    if (window.CEF && window.CEF.call) {
      window.CEF.call("W2C:Menu_F4_Settings_Secure_Secure:Destroyed");
    }
  }

  if (typeof window !== 'undefined') {
    onMount();
  }

  const handleSetData = (newData) => {
    data = { ...data, ...newData };
  };

const handleConnectorClick = (type, status) => {
    if (type === 'google' && !status) {
      showGoogleAuthModal = true;
    } else if (type === 'telegram' && !status) {
      showTelegramModal = true;
    } else if (type === 'phone' && !status) {
      showPhoneModal = true;
    } else if (window.CEF && window.CEF.call) {
      window.CEF.call("W2C:Menu_F4_Settings_Secure_Secure:ToggleConnector", {
        type,
        status
      });
    }
  };
  const handleCredentialChange = (type) => {
    if (type === 'email') {
      showChangeEmailModal = true;
    } else if (type === 'password') {
      showChangePasswordModal = true;
    }
  };
   const closeTelegramModal = () => {
    showTelegramModal = false;
  };
const closeGoogleAuthModal = () => {
    showGoogleAuthModal = false;
  };
  const closeEmailModal = () => {
    showChangeEmailModal = false;
  };

  const closePasswordModal = () => {
    showChangePasswordModal = false;
  };
  const closePhoneModal = () => {
    showPhoneModal = false;
  };
</script>
{#if showPhoneModal}
  <EnablePhoneModal on:close={closePhoneModal} />
{/if}
{#if showTelegramModal}
  <EnableTelegramModal on:close={closeTelegramModal} />
{/if}

{#if showChangeEmailModal}
  <ChangeEmailModal on:close={closeEmailModal} />
{/if}

{#if showChangePasswordModal}
